document.addEventListener("DOMContentLoaded",function(){document.documentElement.classList.remove("no-js");const t=document.querySelector(".dropbtn"),e=document.querySelector(".dropdown-content");t&&e&&(t.addEventListener("click",function(t){t.preventDefault(),t.stopPropagation(),e.classList.toggle("show")}),document.addEventListener("click",function(t){!t.target.matches(".dropbtn")&&e.classList.contains("show")&&e.classList.remove("show")}));const n=document.querySelector(".mobile-menu-toggle"),s=document.querySelector(".main-nav");n&&s&&n.addEventListener("click",function(){s.classList.toggle("active"),this.classList.toggle("active")});const o=document.querySelectorAll("img[data-src]");if("IntersectionObserver"in window){const e=new IntersectionObserver((t)=>{t.forEach(t=>{if(t.isIntersecting){const n=t.target;n.src=n.dataset.src,n.onload=()=>{n.style.backgroundImage="none"},e.unobserve(n)}})});o.forEach(t=>{e.observe(t)})}else o.forEach(e=>{e.src=e.dataset.src})})